from sympy import *
import numpy as np

def get_roots(poly, epsilon):
    x = Symbol('x')
    roots = []
    for i in range(10):
        roots.append(solve(poly.subs(epsilon, 1/epsilon**i), x)[0])
    return np.array(roots)

def main():
    poly = Symbol('x')**9 + Symbol('x')**5 + 1
    small_eps_roots = get_roots(poly, Symbol('epsilon'))
    large_eps_roots = get_roots(poly, 1/Symbol('epsilon'))
    print('Roots for small positive epsilon:')
    print(small_eps_roots)
    print('Roots for large positive epsilon:')
    print(large_eps_roots)

if __name__ == '__main__':
    main()
